package com.example.root.movieuiux.database;import android.content.Context;import android.database.sqlite.SQLiteDatabase;import android.database.sqlite.SQLiteOpenHelper;import static com.example.root.movieuiux.database.DatabaseContract.TABLE_FAVORITE;public class DatabaseHelper extends SQLiteOpenHelper{    public static String DB_NAME        = "movie";    public static final int DB_VERSION  = 1;    private static final String CREATE_TABLE = String.format("CREATE TABLE %s" +            "(%s INTEGER PRIMARY KEY AUTOINCREMENT, " +            "%s TEXT NOT NULL," +            "%s TEXT NOT NULL," +            "%s TEXT NOT NULL," +            "%s TEXT NOT NULL," +            "%s TEXT NOT NULL," +            "%s TEXT NOT NULL," +            "%s TEXT NOT NULL)",            DatabaseContract.TABLE_FAVORITE,            DatabaseContract.FavoriteColumns.TITLE,            DatabaseContract.FavoriteColumns.RELEASE,            DatabaseContract.FavoriteColumns.OVERVIEW,            DatabaseContract.FavoriteColumns.POSTER,            DatabaseContract.FavoriteColumns.BACKDROP,            DatabaseContract.FavoriteColumns.RATE,            DatabaseContract.FavoriteColumns.RATE_AVG,            DatabaseContract.FavoriteColumns.POPULARITY);    public DatabaseHelper(Context context) {        super(context, DB_NAME, null, DB_VERSION);    }    @Override    public void onCreate(SQLiteDatabase db) {        db.execSQL(CREATE_TABLE);    }    @Override    public void onUpgrade(SQLiteDatabase db, int oldVersion, int newVersion) {        db.execSQL("DROP TABLE IF EXISTS "+TABLE_FAVORITE);        onCreate(db);    }}